import React, { useState, useEffect } from 'react'
import Todo from './Todo'

const alltodos = [
  {"descrition": "Einkaufen", "done": true},
  {"descrition": "Sport", "done": false},
  {"descrition": "Lernen", "done": false},
  {"descrition": "Entspannen", "done": true}
];

const TodoList = () => {  
  const [opencount, setCountOpenTodos] = useState(0);
  const [todos, setTodos] = useState(alltodos);

  const countOpen = () => {
    const donetodos = todos.filter((todo) => {
        return !todo.done;
    })

    setCountOpenTodos(donetodos.length);
  }

  useEffect(() => {
    countOpen();
  }, [opencount]);

  const changeTodo = (index) => {
    const newTodos = [...todos];
    if (newTodos[index].done) {
      newTodos[index].done = false;
    }
    else {
      newTodos[index].done = true;
    }

    setTodos(newTodos);
  }


  return (
    <div className="shadow-sm hover:shadow-lg">
      <div className="text-center bg-gray-900 text-white text-3xl py-4 font-semibold">
        <h1 className="text-xl">Unsere Todos</h1>
        <h2>Offene todos: {opencount}</h2>
      </div>
      {todos.map((item, index) => {
        return <Todo 
          key={index} 
          description={item.descrition} 
          done={item.done}
          onChangeTodo={changeTodo}
          index={index}
        ></Todo>;
      })}
    </div>
  )
}

export default TodoList
